mapper.select:

SELECT COMPANY_NO, COMPANY_NAME, COMPANY_SORT, COMPANY_CITY, COMPANY_IMG
FROM COMPANY
WHERE COMPANY_CITY = #{company_city}

mapper.update:
UPDATE COMPANY
SET COMPANY_NAME = #{company_name}, COMPANY_CONTENT = #{company_content}
, COMPANY_TEL = #{company_tel}, COMPANY_ADDR = #{company_addr}
WHERE COMPANY_NO = #{company_no}
